// Vuex 때 처럼 create* 함수를 제공한다.
import { createWebHistory, createRouter } from 'vue-router';

const routes = [
  {
    path: '/',
    name: 'Header',
    component: () => import('@/components/onHeader'), // 동적 import
  },
  {
    path: '/login',
    name: 'Login',
    component: () => import('@/components/testLogin'),
  },
    {
    path: '/board',
    name: 'board',
    component: () => import('@/components/testBoard'), children: [
        {path:'/c/:cid' , component: () => import('@/components/testCard')}
    ]
  },
];

// 이렇게 해도 된다.
// const router = createRouter({
//   history: createWebHistory(),
//   routes,
// });
// export default router;

export const router = createRouter({
  history: createWebHistory(),
  routes,
});
